mapper.update:
UPDATE ITEM SET
ITEMNAME=(
CASE

	WHEN ITEMNO
	= #{obj.itemno} THEN #{obj.itemname}

END
),
ITEMPRICE=(
CASE

	WHEN ITEMNO
	= #{obj.itemno} THEN #{obj.itemprice}

END
),
ITEMQTY=(
CASE

	WHEN ITEMNO = #{obj.itemno} THEN #{obj.itemqty}

END
),
ITEMDES=(
CASE

	WHEN ITEMNO =#{obj.itemno} THEN #{obj.itemdes}

END
)
WHERE ITEMNO IN (

	#{obj.itemno}

)
